package day7;

import java.util.Iterator;

public class NestedForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("i  j");
		for (int i = 1; i  <= 3; i++){
			
			 for (int j = 1; i <=3; j++) {
				 System.out.println(i + " " + j);
				 
			 }
			 System.out.println("#####################");
			 
		 }
		
		System.out.println("#####################");
		
	}
	
}
